import React, { useReducer, useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Route,
  Link,
  Switch,
  Redirect,
  useHistory
} from "react-router-dom";
// import Identification from "./components/Identification";
// import MenuPrincipal from "./components/MenuPrincipal";
// import NouveauDossier from "./components/NouveauDossier";
// import GérerUnDossier from "./components/GérerUnDossier";
// import Dossier from "./components/Dossier";
// import ArchiverUnDossier from "./components/ArchiverUnDossier";
// import ChangerPropriétaire from "./components/ChangerPropriétaire";
// import ChangerMotDePasse from "./components/ChangerMotDePasse";
// import GestionComptesBénévoles from "./components/GestionComptesBénévoles";
// import Statistiques from "./components/Statistiques";
// import ConsulterUneFiche from "./components/ConsulterUneFiche";
// import ModifierUneFiche from "./components/ModifierUneFiche";
// import Fiche from "./components/Fiche";
// import Membre from "./components/Membre";
import "./scss/style.scss";

function App() {

  const [nameInput, setNameInput] = useState("");
  const [emailInput, setEmailInput] = useState("");
  const [commentInput, setCommentInput] = useState("");

  return (
    <div className="commentsModule">
      <p className="title">Comments</p>
      <div className="commentsHistory">
        <div>
          <p>15 February 2012</p>
          <p>By <a>Jo Blog</a></p>
          <p>blablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablabla</p>
        </div>
        <div>
          <p>15 February 2012</p>
          <p>By <a>Jo Blog</a></p>
          <p>blablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablabla</p>
        </div>
        <div>
          <p>15 February 2012</p>
          <p>By <a>Jo Blog</a></p>
          <p>blablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablabla</p>
        </div>
        <div>
          <p>15 February 2012</p>
          <p>By <a>Jo Blog</a></p>
          <p>blablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablabla</p>
        </div>
      </div>
      <p className="title">Leave a Comment</p>
      <form>
        <label>Your name *</label>
        <input type="text" placeholder="Phil Leggeter" name="nameInput" value={nameInput} onChange={e => setNameInput(e.target.value)}/>
        <label>Your email *</label>
        <input type="text" name="emailInput" value={emailInput} onChange={e => setEmailInput(e.target.value)}/>
        <label>Comments</label>
        <textarea type="text" name="commentInput" value={commentInput} onChange={e => setCommentInput(e.target.value)}></textarea>
        <button>Submit comment</button>
      </form>
    </div>
  );
}

export default App;
