import React, { useState, useEffect } from "react";
import "./scss/style.scss";

function App() {

  const [nameInput, setNameInput] = useState("");
  const [emailInput, setEmailInput] = useState("");
  const [commentInput, setCommentInput] = useState("");
  const [comments, setComments] = useState("");

  useEffect(() => {
    checkMode();
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();

    if (!nameInput || !emailInput || !commentInput) {
      alert("All fields of the form must be completed to submit a comment.");
      return;
    }

    const requestOptions = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({'name':nameInput, 'email':emailInput, 'comment':commentInput})
    };

    fetch("/saveComment", requestOptions)
      .then((res) => res.json())
      .then((data) => {
        alert(data);
      }, []);
  }

  return (
    <div className="commentsModule">
      <p className="title">Comments</p>
      <div className="commentsHistory">
        <div>
          <div>
            <p>15 February 2012</p>
            <p>By <a>Jo Blog</a></p>
          </div>
          <p>blablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablabla</p>
        </div>
        <div>
          <div>
            <p>15 February 2012</p>
            <p>By <a>Jo Blog</a></p>
          </div>
          <p>blablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablabla</p>
        </div>
        <div>
          <div>
            <p>15 February 2012</p>
            <p>By <a>Jo Blog</a></p>
          </div>
          <p>blablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablabla</p>
        </div>
        <div>
          <div>
            <p>15 February 2012</p>
            <p>By <a>Jo Blog</a></p>
          </div>
          <p>blablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablabla</p>
        </div>
      </div>
      <p className="title">Leave a Comment</p>
      <form onSubmit={handleSubmit}>
        <label>Your name *</label>
        <input type="text" placeholder="Phil Leggeter" name="name" value={nameInput} onChange={e => setNameInput(e.target.value)}/>
        <label>Your email *</label>
        <input type="text" name="email" value={emailInput} onChange={e => setEmailInput(e.target.value)}/>
        <label>Comments</label>
        <textarea type="text" name="comment" value={commentInput} onChange={e => setCommentInput(e.target.value)}></textarea>
        <button type="submit">Submit comment</button>
      </form>
    </div>
  );
}

export default App;
