import React, { useReducer, useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Route,
  Link,
  Switch,
  Redirect,
  useHistory
} from "react-router-dom";
import "./scss/style.scss";

function App() {

  const [nameInput, setNameInput] = useState("");
  const [emailInput, setEmailInput] = useState("");
  const [commentInput, setCommentInput] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();

    if (!nameInput || !emailInput || !commentInput) {
      alert("All fields of the form must be completed to submit a comment.");
      return;
    }

    const requestOptions = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({'name':nameInput, 'email':emailInput, 'comment':commentInput})
    };

    fetch("/saveComment", requestOptions)
      .then((res) => res.json())
      .then((data) => {
        if (data["status"] == "Error") {
          alert(data["data"]);
