          </div>
          <p>blablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablabla</p>
        </div>
        <div>
          <div>
            <p>15 February 2012</p>
            <p>By <a>Jo Blog</a></p>
          </div>
          <p>blablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablabla</p>
        </div>
        <div>
          <div>
            <p>15 February 2012</p>
            <p>By <a>Jo Blog</a></p>
          </div>
          <p>blablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablablabla</p>
        </div>
      </div>
      <p className="title">Leave a Comment</p>
      <form onSubmit={handleSubmit}>
        <label>Your name *</label>
        <input type="text" placeholder="Phil Leggeter" name="name" value={nameInput} onChange={e => setNameInput(e.target.value)}/>
        <label>Your email *</label>
        <input type="text" name="email" value={emailInput} onChange={e => setEmailInput(e.target.value)}/>
        <label>Comments</label>
        <textarea type="text" name="comment" value={commentInput} onChange={e => setCommentInput(e.target.value)}></textarea>
        <button type="submit">Submit comment</button>
      </form>
    </div>
  );
}

export default App;
