import React, { useReducer } from 'react';

export const formReducer = (state, event) => {
   return {
     ...state,
     [event.name]: event.value
   }
}

export function replaceNullValuesWithEmptyStrings(object) {
  for (var property in object) {
    if (object.hasOwnProperty(property)) {
      if(object[property] == null) {
        object[property] = "";
      }
    }
  }
}

export function datetimeToDatetimeLocal(date) {
  var
      date = new Date(date),
      ten = function (i) {
        return (i < 10 ? '0' : '') + i;
      },
      YYYY = date.getFullYear(),
      MM = ten(date.getMonth() + 1),
      DD = ten(date.getDate()),
      HH = ten(date.getHours()),
      II = ten(date.getMinutes()),
      SS = ten(date.getSeconds())
    ;
    return YYYY + '-' + MM + '-' + DD + 'T' +
             HH + ':' + II + ':' + SS;
}

export function datetimeLocalToDatetime(date) {
  let dateProcessing = date.replace('T', '-').replace(':', '-').split('-');
  let dateOut = dateProcessing[0] + '-' + dateProcessing[1] + '-' + dateProcessing[2] + ' ' + dateProcessing[3] + ':' + dateProcessing[4];
  return dateOut;
}

export function filterDictByParameter(dict, sortby, direction) {
  var filtered = Object.fromEntries(Object.entries(dict).filter(([sortby,v]) => v>1));
  console.log(filtered);
}
